export declare const repoInfoQuery = "\nquery($owner: String!, $name: String!) {\n  repository(owner: $owner, name: $name) {\n    isFork\n    isArchived\n    nameWithOwner\n    hasIssuesEnabled\n    autoMergeAllowed\n    mergeCommitAllowed\n    rebaseMergeAllowed\n    squashMergeAllowed\n    defaultBranchRef {\n      name\n      target {\n        oid\n      }\n    }\n  }\n}\n";
export declare const closedPrsQuery = "\nquery($owner: String!, $name: String!, $count: Int, $cursor: String) {\n  repository(owner: $owner, name: $name) {\n    pullRequests(\n      states: [CLOSED, MERGED],\n      orderBy: {\n        field: UPDATED_AT,\n        direction: DESC\n      },\n      first: $count,\n      after: $cursor\n    ) {\n      pageInfo {\n        endCursor\n        hasNextPage\n      }\n      nodes {\n        number\n        state\n        headRefName\n        title\n        comments(last: 100) {\n          nodes {\n            databaseId\n            body\n          }\n        }\n      }\n    }\n  }\n}\n";
export declare const openPrsQuery = "\nquery($owner: String!, $name: String!, $count: Int, $cursor: String) {\n  repository(owner: $owner, name: $name) {\n    pullRequests(\n      states: [OPEN],\n      orderBy: {\n        field: UPDATED_AT,\n        direction: DESC\n      },\n      first: $count,\n      after: $cursor\n    ) {\n      pageInfo {\n        endCursor\n        hasNextPage\n      }\n      nodes {\n        number\n        headRefName\n        baseRefName\n        title\n        labels(last: 100) {\n          nodes {\n            name\n          }\n        }\n        assignees {\n          totalCount\n        }\n        reviewRequests {\n          totalCount\n        }\n        body\n      }\n    }\n  }\n}\n";
export declare const getIssuesQuery = "\nquery(\n  $owner: String!,\n  $name: String!,\n  $user: String!,\n  $count: Int,\n  $cursor: String\n) {\n  repository(owner: $owner, name: $name) {\n    issues(\n      orderBy: { field: UPDATED_AT, direction: DESC },\n      filterBy: { createdBy: $user },\n      first: $count,\n      after: $cursor\n    ) {\n      pageInfo {\n        endCursor\n        hasNextPage\n      }\n      nodes {\n        number\n        state\n        title\n        body\n      }\n    }\n  }\n}\n";
export declare const vulnerabilityAlertsQuery: (filterByState: boolean) => string;
export declare const enableAutoMergeMutation = "\nmutation EnablePullRequestAutoMerge(\n  $pullRequestId: ID!,\n  $mergeMethod: PullRequestMergeMethod!,\n) {\n  enablePullRequestAutoMerge(\n    input: {\n      pullRequestId: $pullRequestId,\n      mergeMethod: $mergeMethod,\n    }\n  ) {\n    pullRequest {\n      number\n    }\n  }\n}\n";
